

public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/**Write a program  that prints a pattern like below.
		*
		**
		***
		****
		*****
		******
		*/
		
		for(int i=1;i<=6;i++)
		{
		for(int star=1;star<=i;star++){
		System.out.print("*");
		}
		System.out.println();
		
		}
		
	}

}
